package com.vaadin.recipes.recipe.gridimagerenderer;

import java.time.LocalDate;
import java.util.Base64;
import java.util.Collection;

import org.vaadin.artur.exampledata.DataType;
import org.vaadin.artur.exampledata.ExampleDataGenerator;

import com.vaadin.flow.component.grid.Grid;
import com.vaadin.flow.data.renderer.LitRenderer;
import com.vaadin.flow.router.Route;
import com.vaadin.recipes.recipe.Metadata;
import com.vaadin.recipes.recipe.Recipe;
import com.vaadin.recipes.recipe.Tag;

@Route("grid-image-renderer")
@Metadata(
    howdoI = "Show an image in a Grid cell",
    description = "Convert byte array aka blob to Base64 encoded to be shown as inline image with TemplateRenderer",
    tags = { Tag.GRID }
)
public class ImageRendererGrid extends Recipe {
    private Grid<Person> grid = new Grid<>(Person.class);

    public static class Person {
        private String firstName, lastName;
        private LocalDate birthDate;
        private static final String image =
            "";

        public byte[] getImage() {
            return Base64.getDecoder().decode(image);
        }

        public LocalDate getBirthDate() {
            return birthDate;
        }

        public void setBirthDate(LocalDate birthDate) {
            this.birthDate = birthDate;
        }

        public String getFirstName() {
            return firstName;
        }

        public void setFirstName(String firstName) {
            this.firstName = firstName;
        }

        public String getLastName() {
            return lastName;
        }

        public void setLastName(String lastName) {
            this.lastName = lastName;
        }
    }

    private static String TRANSPARENT_GIF_1PX =
        "data:image/gif;base64,R0lGODlhAQABAIAAAP///wAAACwAAAAAAQABAAACAkQBADs=";

    private String getImageAsBase64(byte[] string) {
        String mimeType = "image/png";
        String htmlValue = null;
        if (string == null) htmlValue = TRANSPARENT_GIF_1PX; else htmlValue =
            "data:" + mimeType + ";base64," + Base64.getEncoder().encodeToString(string);
        return htmlValue;
    }

    private Collection<Person> createExamplePersons(int count) {
        ExampleDataGenerator<Person> generator = new ExampleDataGenerator<>(Person.class, 123);
        generator.setData(Person::setFirstName, DataType.FIRST_NAME);
        generator.setData(Person::setLastName, DataType.LAST_NAME);
        generator.setData(Person::setBirthDate, DataType.DATE_OF_BIRTH);
        return generator.create(count);
    }

    public ImageRendererGrid() {
        // Setup a grid with random data
        grid.setColumns("birthDate", "firstName", "lastName");
        grid.setItems(createExamplePersons(100));
        grid.addColumn(
            LitRenderer
                .<Person>of(
                    "<div><img style='height: 80px; width: 80px;' src=${item.imagedata} alt=${item.name}></div>"
                )
                .withProperty("imagedata", item -> getImageAsBase64(item.getImage()))
                .withProperty("name", item -> item.getFirstName() + " " + item.getLastName())
        );

        add(grid);
    }
}
